binding.script:
var flapsAZS = getprop("an24/AZS/sw0305");
var flapsAZSpower = getprop("an24/Electrics/AZSemerg_V");
if (flapsAZS == 1.0 and flapsAZSpower > 24.0) {
	setprop("an24/UZP-1/flap-command", 1.0 );
	var flaps_min = 0;
	setprop("/controls/flight/flaps", flaps_min);
}

var flapsAZS = getprop("an24/AZS/sw0305");
var flapsAZSpower = getprop("an24/Electrics/AZSemerg_V");
if (flapsAZS == 1.0 and flapsAZSpower > 24.0) {
	setprop("an24/UZP-1/flap-command", 0.0 );
	var flaps_current = getprop("/surface-positions/flap-pos-norm");
	setprop("/controls/flight/flaps", flaps_current);
}

var flapsAZS = getprop("an24/AZS/sw0305");
var flapsAZSpower = getprop("an24/Electrics/AZSemerg_V");
if (flapsAZS == 1.0 and flapsAZSpower > 24.0) {
	setprop("an24/UZP-1/flap-command", 1.0 );
	var flaps_max = 1;
	setprop("/controls/flight/flaps", flaps_max);
}

var flapsAZS = getprop("an24/AZS/sw0305");
var flapsAZSpower = getprop("an24/Electrics/AZSemerg_V");
if (flapsAZS == 1.0 and flapsAZSpower > 24.0) {
	setprop("an24/UZP-1/flap-command", 0.0 );
	var flaps_current = getprop("/surface-positions/flap-pos-norm");
	setprop("/controls/flight/flaps", flaps_current);
}


	# the appropriate circuit breaker must be pushed in:
	if (getprop ("an24/AZS/sw0317") == 0) return;
		var current_mode = getprop ("an24/gear/steer_mode");
		if (current_mode == 0) {
		# transition from TAXI to OFF.
		interpolate("an24/gear/steer_mode", 1.0, 0.1);
		setprop("controls/gear/nose-wheel-steering", 0.0);
		setprop("fdm/jsbsim/gear/nose-wheel-steering", 0.0);
	}
	else if (current_mode == 1) {
		# transition from OFF to TAKEOFF/LANDING
		interpolate("an24/gear/steer_mode", 2.0, 0.1);
		setprop("controls/gear/nose-wheel-steering", 1.0);
 		setprop("fdm/jsbsim/gear/nose-wheel-steering", 1.0);
		setprop("controls/gear/steering", 10.0);
		setprop("fdm/jsbsim/gear/steering", 10.0);
 	}
	else if (current_mode == 2) {
 		# transition from TAKEOFF/LANDING to TAXI.
		interpolate("an24/gear/steer_mode", 0.0, 0.1);
		setprop("controls/gear/nose-wheel-steering", 1.0);
		setprop("fdm/jsbsim/gear/nose-wheel-steering", 1.0);
		setprop("controls/gear/steering", 45.0);
		setprop("fdm/jsbsim/gear/steering", 45.0);
	}
